#!/usr/bin/env python

import matplotlib.pyplot as plt
import argparse

# This script is designed to allow user to review manually 
# the putative T3 inc effectors generated by the annotation pipeline
# (specifically, the T3SS_inc_proteins_detection)
# It is not included in the base pipeline as it needs to be interactive.

parser = argparse.ArgumentParser()
parser.add_argument("-f", "--fasta", type=str, help="fasta file containing the sequences")
parser.add_argument("-v", "--values", type=str, help="file containing the hydropathy values, as generated by the annotation pipeline")
parser.add_argument("-o", "--output", type=str, help="output file (optional)")

args = parser.parse_args()


val_file = open(args.fasta, "r")

line = val_file.readline()
records_to_keep = []

while line:
    if line[0]!=">":
        line = val_file.readline()
        continue

    line = val_file.readline()
